import React from 'react'
import { Link } from 'react-router-dom';

const BILL = () => {
    return (
        <>
            <div className='container' style={cont}>
                <div style={{ textAlign: "center" }}>
                    <Link to='/' className='btn ' style={btn}> &#8592; Go Back</Link>
                    <Link to='/BMI' className='btn ' style={btn}>BMI Calculation</Link>
                    <Link to='/EMI' className='btn ' style={btn}>EMI Calculation</Link>
                </div>
                <h1 style={{ textAlign: "center", marginTop: "25px", textDecoration: "underline" }}>BILL Calculator</h1>
                <BillDisplay/>
            </div>
        </>
    )
}
export default BILL;

const BillDisplay = () => {
let unit;
    const calculate = (e) => {
        unit = Number(document.querySelector("#unit").value);
        inpW = Number(document.querySelector("#weight").value);
        win = document.getElementById("win").value;
        hin = document.getElementById("hin").value;
        if (win === "-1" || hin === "-1") {
            document.querySelector("#result").innerHTML = "Please Select All Unit fields for Calculation";
            document.querySelector("#result").style = "color:red;font-size:20px;margin-top:15px;text-align:center";
        } else if (inpH === "" || inpW === "") {
            document.querySelector("#result").innerHTML = "Please Fill all fields.";
            document.querySelector("#result").style = "color:red;font-size:20px;margin-top:15px;text-align:center;";
