        }
    }

    return (
        <>
            <div style={bmi}>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label" style={{ fontWeight: "800", fontSize: "1.6em",color:"blue" }} >Electricity Used -</label>
                    <input type="number" class="form-control" id="unit" placeholder="Enter units" />
                </div>
            </div>
            <button className='btn btn-dark' style={btn} id="calculate" onClick={calculate}> Calculate</button>
                <h2 id="result"> </h2>
                <h3 id="bm"> </h3>
        </>
    )
}

