import React from 'react'
import { Link } from 'react-router-dom';

const EMI = () => {
    return (
        <>
            <div style={{ background: "url(https://mir-s3-cdn-cf.behance.net/project_modules/fs/4c032e28008799.56e41b3bbd979.gif)", backgroundSize: "cover" }}>
                <div className='container' style={cont}>
                    <div style={{ textAlign: "center" }}>
                        <Link to='/' className='btn btn-outline-dark' style={btn}> &#8592; Go Back</Link>
                        <Link to='/BMI' className='btn btn-outline-dark' style={btn}>BMI Calculation</Link>
                        <Link to='/BILL' className='btn btn-outline-dark' style={btn}>BILL Calculation</Link>
                    </div>
                    <h1 style={{ textAlign: "center", marginTop: "40px", color: "#1a759f" }}>EMI Calculator</h1>
                    <EmiDisplay />
                </div>
            </div>
        </>
    )
}

export default EMI
const EmiDisplay = () => {
    let amount,interest,month,year,monthly,anually;


    const calculate = () => {
        amount = document.querySelector('#amount').value;
        interest = document.querySelector('#interest').value;

    }

    const reset = () => {

    }

    const details = () => {

    }

    return (
        <>
            <div style={emi}>
                <div class="mb-3">
                    <label class="form-label" style={{ fontWeight: "800", fontSize: "1.3em" }} >Loan Amount</label>
                    <input type="number" class="form-control" id="amount" placeholder="Enter Amount" style={span} />
                    <label class="form-label" style={{ fontWeight: "800", fontSize: "1.3em" }}>Interest %</label>
                    <input type="number" class="form-control" id="interest" placeholder="Enter Rate " style={span} />
                    <label class="form-label" style={{ fontWeight: "800", fontSize: "1.3em" }}>Period</label>
                    <span style={{ marginLeft: "16%" }}> <input type="number" class="form-control" id="year" placeholder="Year " style={{ width: "25%", display: "inline" }} />
                        <input type="number" class="form-control" id="month" placeholder="Months " style={{ width: "25%", display: "inline" }} /></span>

                    <label class="form-label" style={{ fontWeight: "800", fontSize: "1.3em", display: "block" }}>EMI</label>
                    <input type="number" disabled class="form-control" id="interest" placeholder="EMI per Month" style={span} />
                </div>
                <div style={{marginLeft:"20%"}}>
                    <button className='btn btn-dark' style={btn} id="calculate" onClick={calculate}> Calculate</button>
                    <button className='btn btn-dark' style={btn} id="reset" onClick={reset}>Reset</button>
                    <button className='btn btn-dark' style={btn} id="details" onClick={details}> Details</button>
                </div>

                <h2 id="result"> </h2>
                <h3 id="bm"> </h3>
            </div>
        </>
    )
}

const cont = {
    backgroundImage: "url(https://marketplace.canva.com/EAD2962NKnQ/2/0/1600w/canva-rainbow-gradient-pink-and-purple-zoom-virtual-background-_Tcjok-d9b4.jpg)",
    border: "1px solid white",
    width: "70%",
    height: "100vh",
    boxShadow: " 0px 0px 102px 25px rgba(80,98,199,1)",
}
const emi = {
    marginLeft: "15%",
    marginTop: "30px"
}
const btn = {
    marginTop: "3vh",
    marginRight: "30px"
}
const span = {
    width: "50%",
    marginLeft: "25%",
    marginTop: "-5%"
}
