import React from 'react'
import { Link } from 'react-router-dom';
import BILL from './BILL';
import EMI from './EMI';
import HOME from './HOME';

const BMI = () => {
    return (
        <>
            <div className='container' style={cont}>
                <div style={{ textAlign: "center" }}>
                    <Link to='/' className='btn btn-outline-dark' style={btn}> &#8592; Go Back</Link>
                    <Link to='/BILL' className='btn btn-outline-dark' style={btn}>BILL Calculation</Link>
                    <Link to='/EMI' className='btn btn-outline-dark' style={btn}>EMI Calculation</Link>
                </div>
                <BmDisplay />
            </div>

        </>

    )
}

export default BMI;


const BmDisplay = () => {
    return (
        <>
            <div style={bmi}>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label" style={{fontWeight:"800"}} >Weight</label>
                    <span ><select class="form-select" aria-label="Default select example">
                        <option selected>Select Weight Unit </option>
                        <option value="kg">Kilogram</option>
                        <option value="pd">Pounds</option>
                    </select></span>
                    <input type="number" class="form-control" id="weight" placeholder="Enter Weight" />
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlTextarea1" class="form-label" style={{fontWeight:"800"}}>Height</label> <span style={span}><select class="form-select" aria-label="Default select example">
                        <option selected>Select Height Unit</option>
                        <option value="cm">Centimeters</option>
                        <option value="in">Inches</option>
                    </select></span>
                    <input type="number" class="form-control" id="height" placeholder="Enter Height" />
                </div>


            </div>

        </>
    )
}

const cont = {
    backgroundImage: "url(https://marketplace.canva.com/EAD2962NKnQ/2/0/1600w/canva-rainbow-gradient-pink-and-purple-zoom-virtual-background-_Tcjok-d9b4.jpg)",
    border: "1px solid white",
    width: "70%",
    height: "90vh",
}
const bmi = {
    marginTop: "10%",
    fontSize:"20px",

}
